import React from 'react'
import './Main.css'
import siluet from './siluet.png'

export default function Main() {
  return (
    <div className='ConInfo'>
        <div>
            <div className='objective'>Frontend Developer</div>
            <ul className='infoUl'>
                <li className='infoLi' onClick={copyToClipboard()}>Phone number <div className='hoverInfo'>+380987343986</div></li>
                <li className='infoLi' onClick={copyToClipboard()}>Gmail <div className='hoverInfo'>nazik.igrek@gmail.com</div></li>
                <li className='infoLi'>Telegram</li>
                <li className='infoLi'>GitHub</li>
            </ul>
        </div>
        <img className='siluet' src={siluet} alt="#" />
    </div>
  )
}

function copyToClipboard() {
    const text = document.getElementsByClassName("infoLi").innerText;
    navigator.clipboard.writeText(text)
      .then(() => {
        alert("Скопировано: " + text);
      })
      .catch(err => {
        alert("Ошибка при копировании: " + err);
      });
    }
