import React from 'react'
import './Main.css'
import siluet from './siluet.png'

export default function Main() {
  return (
    <div className='ConInfo'>
        <div className='CopyDone'>sdfdf</div>
        <div>
            <div className='objective'>Frontend Developer</div>
            <ul className='infoUl'>
                <li className='infoLi'>Phone number <div className='hoverInfo'>+380987343986</div></li>
                <li className='infoLi'>Gmail <div className='hoverInfo'>nazik.igrek@gmail.com</div></li>
                <li className='infoLi TG' onClick={handleClick1}>Telegram</li>
                <li className='infoLi GHub' onClick={handleClick2}>GitHub</li>
            </ul>
        </div>
        <img className='siluet' src={siluet} alt="#" />
    </div>
  )
}

let timerId = null;
document.querySelectorAll('.infoLi').forEach(item => {
    item.addEventListener('click', () => {
      const info = item.querySelector('.hoverInfo');
      if (info) {
        navigator.clipboard.writeText(info.innerText)
          .then(() => {
            if (timerId) {
                clearTimeout(timerId);
            }    
            const targetElement = document.getElementsByClassName('CopyDone');
            targetElement.textContent = "copied: " + info.innerText;
            targetElement.id = 'CopyDoneVis'
            timerId = setTimeout(() => {
                targetElement.id = '';
            }, 1000);
            timerId = null;
          })
          .catch(err => {
            console.error("error:", err);
          });
      }
    });
  });

const handleClick1 = () => {
    window.open('https://t.me/Ha3apiKyc', '_blank', 'noopener,noreferrer');
};
const handleClick2 = () => {
    window.open('https://github.com/NazarAtamaniuk', '_blank', 'noopener,noreferrer');
};
