import React from 'react'
import './Main.css'
import siluet from './siluet.png'
import ConInfo from './ConInfo/ConInfo';

export default function Main() {
  return (
    <ConInfo/>
  )
}

let timerId = null;
document.querySelectorAll('.infoLi').forEach(item => {
    item.addEventListener('click', () => {
      const info = item.querySelector('.hoverInfo');
      if (info) {
        navigator.clipboard.writeText(info.innerText)
          .then(() => {
            if (timerId) {
                clearTimeout(timerId);
            }    
            const targetElement = document.querySelector('.CopyDone');
            targetElement.textContent = "copied: " + info.innerText;
            targetElement.id = 'CopyDoneVis'
            timerId = setTimeout(() => {
                targetElement.id = '';
            }, 1500);
            timerId = null;
          })
          .catch(err => {
            console.error("error:", err);
          });
      }
    });
  });

const handleClick1 = () => {
    window.open('https://t.me/Ha3apiKyc', '_blank', 'noopener,noreferrer');
};
const handleClick2 = () => {
    window.open('https://github.com/NazarAtamaniuk', '_blank', 'noopener,noreferrer');
};
